INSERT INTO `wp_posts` VALUES (1894,1,'2021-10-28 15:28:38','2021-10-28 15:28:38','<!-- wp:html -->\n<div id=\"primary\">\n        <div id=\"main homepage\">\n            <div class=\"content\">\n                <div id=\"homepage_header\">\n                    <h1><strong>Piloting software solution for your climate & &amp; CSR strategy</strong><br>\n                    </h1>\n                    <h2><em>And if it was time to change era?</em></h2>\n                    <img class=\"text-center\" src=\"/wp-content/uploads/2021/04/toovalu-impact.jpeg\" alt=\"logo-toovalu-impact\" aria-label=\"logo-toovalu-impact\">\n                 \n            </div>\n        </div></div></div>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<div class=\"content\">\n        <img class=\"text-center wdt-100\" src=\"/wp-content/uploads/2021/06/1024x512px_Votre_empreinte_carbone_va_en_prendre_un_coup.png\" alt=\"toovalu-votre-empreinte-carbone-va-en-prendre-en-compte\" aria-label=\"claims-presentation-toovalu\">\n    </div>\n   \n<div class=\"content\">\n        <div class=\"container pt-2 pb-2\">\n            <h2 class=\"text-center\">The tool that takes you <em>beyond</em> reporting</h2>\n            <h3 class=\"text-center\">Toovalu guides you with the analysis of data<br> figures,\n                significant impacts\n            </h3>\n            <div>\n                <div class=\"flex-space-around pb-2\">\n                    <a class=\"pink-visited\" href=\"#dynamique\">Dynamic dashboards</a>\n                    <a class=\"pink-visited\" href=\"#collecte\"> Strategic directions</a>\n                    <a class=\"pink-visited\" href=\"#analyse\"> Your Piloting indicators</a>\n                </div>\n                <div class=\"carousel\">\n                    <ul class=\"slides\">\n                        <input type=\"radio\" name=\"radio-buttons\" id=\"img-1\" checked=\"\">\n                        <li class=\"slide-container\" id=\"dynamique\">\n                            <div c